mapper.select:
SELECT user_id
FROM post_tb
WHERE post_id = #{postId}

mapper.delete:
DELETE FROM post_tb
WHERE post_id = #{postId}
AND user_id = #{userId}

DELETE FROM like_tb
WHERE user_id = #{userId}
AND post_id = #{postId}

mapper.update:
UPDATE post_tb
SET like_count = (
SELECT COUNT(*) FROM like_tb WHERE post_id = #{postId}
)
WHERE post_id = #{postId}

mapper.select:
SELECT EXISTS (
SELECT 1
FROM post_tb
WHERE preset_id = #{presetId}
)

SELECT EXISTS (
SELECT 1
FROM post_tb
WHERE user_id = #{userId}
AND preset_id = #{presetId}
)
