mapper.delete:
UPDATE preset_tb
SET is_deleted = 1
WHERE preset_id = #{presetId}
AND user_id = #{userId}

mapper.select:
SELECT product_id
FROM preset_tb
WHERE preset_id = #{presetId}

SELECT EXISTS (
SELECT 1
FROM preset_tb
WHERE user_id = #{userId}
AND product_id = #{productId}
AND is_deleted = 0
)
